package com.display_series;
import java.util.*;
 
public class Display_Series_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in );
		System.out.println("Enter term");
		int num=sc.nextInt();
		for(int i=1;i<=num;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("1");
			}
			System.out.print(",");
		}
	
	}
	}
 
 

